GetWishlistQuery:
query GetWishlistQuery($email: String) {
  getWishlist(email: $email) {
    id
    productIds
    email
  }
}

SetWishlistMutation:
mutation SetWishlistMutation($email: String, $productIds: String, $id: String) {
  setWishlist(email: $email, productIds: $productIds, id: $id) {
    message
  }
}

GetWishListProducts:
query GetWishListProducts($productIds: String) {
  getWishListProducts(productIds: $productIds) {
    allInstallment {
      Value
      TotalValuePlusInterestRate
      PaymentSystemName
      PaymentSystemGroupName
      NumberOfInstallments
      Name
      InterestRate
    }
    discountHighlights {
      name
    }
    brand
    image
    listPrice
    link
    price
    productId
    productName
  }
}

MenuCategoryQuery:
query MenuCategoryQuery($first: Int!) {
  allCollections(first: $first) {
    edges {
      node {
        type
        breadcrumbList {
          itemListElement {
            name
            item
            position
          }
        }
      }
    }
  }
}

NewsLetterQueryUpdate:
mutation NewsLetterQueryUpdate($email: String!, $id: String) {
  newsLetterUpdate(email: $email, id: $id) {
    id
  }
}

ShippingQuery:
query ShippingQuery($country: String, $items: [ShippingItem], $postalCode: String!) {
  shipping(postalCode: $postalCode, items: $items, country: $country) {
    logisticsInfo {
      slas {
        name
        pickupStoreInfo {
          friendlyName
          isPickupStore
        }
        shippingEstimate
        price
      }
    }
  }
}

ProductGalleryQuery:
query ProductGalleryQuery($first: Int!, $after: String!, $sort: StoreSort!, $term: String!, $selectedFacets: [IStoreSelectedFacet!]!) {
  search(
    first: $first
    after: $after
    sort: $sort
    term: $term
    selectedFacets: $selectedFacets
  ) {
    products {
      pageInfo {
        totalCount
      }
    }
    facets {
      ... on StoreFacetRange {
        key
        label
        min {
          selected
          absolute
        }
        max {
          selected
          absolute
        }
        __typename
      }
      ... on StoreFacetBoolean {
        key
        label
        values {
          label
          value
          selected
          quantity
        }
        __typename
      }
    }
  }
}

ServerCollectionPageQuery:
query ServerCollectionPageQuery($slug: String!) {
  collection(slug: $slug) {
    seo {
      title
      description
    }
    breadcrumbList {
      itemListElement {
        item
        name
        position
      }
    }
    meta {
      selectedFacets {
        key
        value
      }
    }
  }
}

ServerProductPageQuery:
query ServerProductPageQuery($slug: String!) {
  product(locator: [{key: "slug", value: $slug}]) {
    id: productID
    seo {
      title
      description
      canonical
    }
    brand {
      name
    }
    sku
    gtin
    name
    description
    releaseDate
    breadcrumbList {
      itemListElement {
        item
        name
        position
      }
    }
    image {
      url
      alternateName
    }
    offers {
      lowPrice
      highPrice
      priceCurrency
      offers {
        availability
        price
        priceValidUntil
        priceCurrency
        itemCondition
        seller {
          identifier
        }
        listPrice
      }
    }
    isVariantOf {
      productGroupID
      name
      hasVariant {
        offers {
          lowPrice
        }
        additionalProperty {
          name
          propertyID
          value
          valueReference
        }
        slug
        image {
          url
          alternateName
        }
      }
      skuVariants {
        activeVariations
        slugsMap(dominantVariantName: "Cor")
        availableVariations(dominantVariantName: "Cor")
      }
    }
    specificationGroups {
      name
      originalName
      specifications {
        values
        originalName
        name
      }
    }
    Sellers {
      sellerDefault
      sellerName
      commertialOffer {
        Installments {
          Value
          InterestRate
          TotalValuePlusInterestRate
          NumberOfInstallments
          Name
          PaymentSystemName
        }
        discountHighlights {
          name
        }
      }
    }
    additionalProperty {
      propertyID
      name
      value
      valueReference
    }
  }
}

