  validateCart(cart: $cart, session: $session) {
    order {
      orderNumber
      acceptedOffer {
        seller {
          identifier
        }
        quantity
        price
        listPrice
        itemOffered {
          sku
          name
          image {
            url
            alternateName
          }
          brand {
            name
          }
          isVariantOf {
            productGroupID
            name
          }
          gtin
          additionalProperty {
            propertyID
            name
            value
            valueReference
          }
        }
      }
    }
    messages {
      text
      status
    }
  }
}

